@udf(returnType = StringType())
def mask_output_data(input_string, output_indexes_to_mask):
    output_string = input_string
    for index_tuple in output_indexes_to_mask:
        output_string = output_string[:index_tuple[0]] + (Config.mask_character*(index_tuple[1]-index_tuple[0]) ) + output_string[index_tuple[1]:]
    return output_string
